package String;

public class swaping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      
		String s1="krishna";
		
		
		String s2="kashid";
		
		String temp=s1;
		
		s1=s2;
		s2=temp;
		
		System.out.println(s1);
		System.out.println(s2);
		
		
	}

}
